        (tan(betta)*L*dr)/r^2 - (tan(betta)*dL)/r - (L*(tan(betta)^2 + 1)*dbetta)/r - dphi;...
        sin(betta)*dT_betta - sin(gama)*dT_gama + P*dr + r*dP + cos(betta)*T_betta*dbetta - cos(gama)*T_gama*dgama];

